sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
    // Verifica se o formulário está carregando ou se o valor não mudou
    if (isLoading || newValue === oldValue) {
        return;
    }

    if (!newValue) {
        g_form.clearValue('city');
        g_form.clearValue('state');
        g_form.clearValue('country');
        g_form.clearValue('time_zone');
        return;
    }

    // Chama o Script Include usando GlideAjax
    var ga = new GlideAjax('x_1540861_ip_api.IPInfoFetcher');
    ga.addParam('sysparm_name', 'getIPDetails');
    ga.addParam('sysparm_ip', newValue);

    ga.getXMLAnswer(function (response) {
        var result;
        try {
            result = JSON.parse(response);
        } catch (e) {
            g_form.addErrorMessage('Erro ao processar a resposta do servidor.');
            return;
        }

        // Verifica se houve um erro na resposta
        if (result.error) {
            g_form.addErrorMessage('Erro ao buscar informações do IP: ' + result.error);
            return;
        }

        // Preenche os campos com os dados retornados
        g_form.setValue('city', result.city || '');
        g_form.setValue('state', result.regionName || '');
        g_form.setValue('country', result.country || '');
        g_form.setValue('time_zone', result.timezone || '');
    });
}
